/*Version 1: uses useSelector to retrieve the todos generated by the todos.js reducer*/
import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";

const Todos = () => {
    const todos = useSelector(state => state.todos);
    // 1 basic state
    const [todo, setTodo] = useState({do: ''}); // create todos local state variable initialized
    // handle keystroke changes in input field
    const todoChangeHandler = (event) => {
        const doValue = event.target.value;
        const newTodo = {
            do: doValue
        };
        // change local state todo variable
        setTodo(newTodo);
    }
    // 2 create a dispatch
    const dispatch = useDispatch();
    // handle create click event
    const createTodoClickHandler = () => {
        const action = {
            type: 'create-todo',
            todo
        };
        // send action to reducers
        dispatch(action);
    }
    // 3 delete todos event handler accepts todo
    const deleteTodoClickHandler = (todo) => {
        // create new action
        const action = {
            type: 'delete-todo',
            todo
        };
        dispatch(action);
    }


    return (
        <>
            <h3>Todos</h3>
            <ul className="list-group">
                {/*// add a new line item at the top*/}
                <li className="list-group-item">
                    <input
                        onChange={todoChangeHandler}
                        value={todo.do}
                        className="form-control"/>
                    {/*// create button notifies event handler when*/}
                    <button onClick={createTodoClickHandler}
                            className="btn btn-primary">
                        Create New Todos
                    </button>

                    <button onClick={() =>
                        deleteTodoClickHandler(todo)}
                            className="btn btn-danger float-end">
                        Delete
                    </button>

                </li>

                {
                    todos.map(todo =>
                        <li className="list-group-item">
                            {todo.do}
                        </li>
                    )
                }
            </ul>
        </>
    );
};
export default Todos;
