// get the useState hook
import React, {useState} from "react";
import {useDispatch} from "react-redux";
import "./style.css";

const WhatsHappening = () => {
    // create whatsHappening state variable
    let [whatsHappening, setWhatsHappening]
        = useState('');
    // handle tuit button click
    const tuitClickHandler = () => {
        dispatch({
            type: 'create-tuit',
            tuit: whatsHappening
        });
    }
    const dispatch = useDispatch();
    return (
        <div className="list-group-item">
            <div>
                <img className="wd-avatar" src="/img/avatar/userav.jpeg" alt="Avatar"/>
            </div>
            <textarea className="wd-whatsHappening"
                      placeholder="What's happening?"
                      value={whatsHappening}
                      onChange={(event) =>
                          setWhatsHappening(event.target.value)}>
            </textarea>
            <a href="../../../../public/tuiter/profile.html" className="list-group-item list-group-item-action">
                <i className="fas fa-user-alt wd-icon"></i>
                <i className="far fa-image-polaroid"></i>
            </a>
            <i className="far fa-image-polaroid"></i>
            {/*// notify Tuit button click*/}
            <button className="btn btn-primary btn-block rounded-pill wd-tuit"
                    onClick={tuitClickHandler}>
                Tuit
            </button>
        </div>
    );
}
export default WhatsHappening;
